      _categoriesItems = [];
      json['categoriesItems'].forEach((v) {
        _categoriesItems?.add(ProductModel.fromJson(v));
      });
    }
  }
  List<ProductModel>? _categoriesItems;
CategoryItemsModel copyWith({  List<ProductModel>? categoriesItems,
}) => CategoryItemsModel(  productsList: categoriesItems ?? _categoriesItems,
);
  List<ProductModel>? get productsList => _categoriesItems;

